import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState, useEffect } from 'react';
import useScreenType from 'react-screentype-hook';
import queryString from 'query-string';

function App() {

  const screenType = useScreenType();
  const [email, setEmail] = useState();

  const [cardState, setCardState] = useState({
    isTitleOpen: true,
    isMenuOpen: false,
    isInfoOpen: false
  });

  useEffect(() => {
    if (screenType.isTablet) {
      setCardState({
        isTitleOpen: false,
        isMenuOpen: true,
        isInfoOpen: true
      })
    }

    const query = queryString.parse(window.location.href);
    setEmail(query);

  }, [])



  const toggleCard = (cardName) => {

    if (screenType.isTablet) {
      setCardState({
        isTitleOpen: !cardState.isTitleOpen,
        isMenuOpen: !cardState.isMenuOpen,
        isInfoOpen: !cardState.isInfoOpen
      })
    } else {
      switch (cardName) {
        case 'title':
          setCardState({
            isTitleOpen: !cardState.isTitleOpen,
            isMenuOpen: false,
            isInfoOpen: false
          });
          break;
        case 'menu':
          setCardState({
            isTitleOpen: false,
            isMenuOpen: !cardState.isMenuOpen,
            isInfoOpen: false
          });
          break;
        case 'info':
          setCardState({
            isTitleOpen: false,
            isMenuOpen: false,
            isInfoOpen: !cardState.isInfoOpen
          });
          break;
        default:
          console.log('Option not identified');
          break;
      }
    }
  }

  return (
    <div>
      <Header toggleCard={toggleCard} />
      <Main state={cardState} email={email} />
    </div>
  );
}

export default App;
