import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState, useEffect } from 'react';

function App() {

  const [cardState, setCardState] = useState({
    isTitleOpen: false,
    isMenuOpen: false,
    isInfoOpen: false
  });


  const toggleCard = (cardName) => {

    switch (cardName) {
      case 'title':
        setCardState({
          isTitleOpen: true,
          isMenuOpen: false,
          isInfoOpen: false
        });
        break;
      case 'menu':
        setCardState({
          isTitleOpen: false,
          isMenuOpen: true,
          isInfoOpen: false
        });
        break;
      case 'info':
        setCardState({
          isTitleOpen: false,
          isMenuOpen: false,
          isInfoOpen: true
        });
        break;
      default:
        console.log('Option not identified');
        break;
    }
  }

  return (
    <div>
      {console.log(cardState)}
      <Header toggleCard={toggleCard} />
      <Main state={cardState} />
    </div>
  );
}

export default App;
