import React from 'react'
import '../styles/Header.css'

function Header() {


    const [isTitleCardOpen, setIsTitleCardOpen] = useState(false)
    const [isMenuCardOpen, setIsMenuCardOpen] = useState(false)
    const [isInfoCardOpen, setIsInfoCardOpen] = useState(false)

    const toggleCard = (cardName) => {
        setIsTitleCardOpen(false);
        setIsMenuCardOpen(false);
        setIsInfoCardOpen(false);

        switch (cardName) {
            case title:
                setIsTitleCardOpen(true);
                break;
            case menu:
                setIsMenuCardOpen(true);
                break;
            case info:
                setIsInfoCardOpen(true);
                break;
        }
    }

    return (
        <div className='header'>
            <div className='title-space'>
                <div className='menu'>
                    <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" className="bi bi-list icon" viewBox="0 0 16 16">
                        <path fillRule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z" />
                    </svg>
                </div>
                <div className='title'>Profiler</div>
            </div>

            <div className='header-profile-pic'>
                <img src='pic.jpg' alt='profile-pic'></img>
            </div>
        </div>
    )
}

export default Header
