import React, { useRef, useEffect, useState } from 'react'
import '../styles/Main.css'
import useScreenType from 'react-screentype-hook';
import axios from 'axios';

function Main(props) {

    const screenType = useScreenType();
    const iconRef = useRef();
    const [menuList, setMenuList] = useState();

    useEffect(() => {
        //iconRef.current.innerHTML = "<svg xmlns='http://www.w3.org/2000/svg' fill='currentColor' className='icon' viewBox='0 0 16 16'>                                                <path d='M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z' />                                            </svg>";

        axios.get('http://localhost:8080/api/getMenus?email=sarkar.soumyabrata2@gmail.com')
            .then(response => {
                setMenuList(response.data);
            })

    }, []);

    return (
        <div className='main'>
            <div className='content-card'>

                {
                    (props.state.isTitleOpen || !screenType.isMobile) ?
                        <div className='title-card card'>
                            <div className='section'>
                                <img className='profile-pic' src='pic.jpg' alt='profile-pic'></img>
                                <span><strong>Soumyabrata Sarkar</strong></span>
                                <span>sarkar.soumyabrata2@gmail.com</span>
                            </div>
                            <ul>
                                <li>
                                    <div className='list-item'>
                                        <div className='icon' ref={iconRef}>
                                            {/* <svg xmlns='http://www.w3.org/2000/svg' fill='currentColor' className='icon' viewBox='0 0 16 16'>
                                                <path d='M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z' />
                                            </svg> */}
                                        </div>
                                        <div className='content'>
                                            <span>github.com</span>
                                        </div>
                                    </div>
                                </li>
                            </ul>
                        </div>
                        : <></>
                }

                {
                    (props.state.isMenuOpen || !screenType.isMobile) ?
                        <div className='sub-content-card card'>
                            <ul>


                                {menuList.map((menu) => {
                                    return (
                                        menu.title !== 'Phone' && menu.title !== 'Address' && menu.title !== 'Link' ?
                                            <li>
                                                <div className='list-item'>
                                                    <div className='icon'>
                                                        <svg xmlns='http://www.w3.org/2000/svg' fill='currentColor' className='icon' viewBox='0 0 16 16'>
                                                            <path d='M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10z' />
                                                        </svg>
                                                    </div>
                                                    <div className='content'>
                                                        <span>{menu.title}</span>
                                                    </div>
                                                    <div className='select'>
                                                        <svg xmlns='http://www.w3.org/2000/svg' fill='currentColor' className='icon' viewBox='0 0 16 16'>
                                                            <path fill-rule='evenodd' d='M4.646 1.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1 0 .708l-6 6a.5.5 0 0 1-.708-.708L10.293 8 4.646 2.354a.5.5 0 0 1 0-.708z' />
                                                        </svg>
                                                    </div>
                                                </div>
                                            </li>
                                            : <></>)
                                })

                                }


                            </ul>
                        </div>
                        : <></>
                }

                {
                    (!props.state.isTitleOpen && !props.state.isMenuOpen) || !screenType.isMobile ?
                        <div className='sub-content-info-card card'>
                            sub content info
                        </div>
                        : <></>
                }
            </div>

        </div>
    )
}

export default Main
