import React, { useEffect, useState } from 'react'
import '../styles/Main.css'
import useScreenType from 'react-screentype-hook';
import axios from 'axios';
import iconMap from '../icon-map.json';
import htmlPerser from 'html-react-parser';
import appProp from '../application';

function Main(props) {

    const screenType = useScreenType();
    const [menuList, setMenuList] = useState();
    const [titleCardContactList, setTitleCardContactList] = useState();
    const [titleCardLinkList, setTitleCardLinkList] = useState();


    const getMenuList = async () => {
        const response = await axios.get(appProp.service_base_url + '/getMenus?email=sarkar.soumyabrata2@gmail.com');
        setMenuList(response.data);
    }

    const getTitleCardContactList = async () => {
        const response = await axios.get(appProp.service_base_url + '/getContacts?email=sarkar.soumyabrata2@gmail.com');
        setTitleCardContactList(response.data);
    }

    const getTitleCardLinkList = async () => {
        const response = await axios.get(appProp.service_base_url + '/getLinks?email=sarkar.soumyabrata2@gmail.com');
        setTitleCardLinkList(response.data);
    }

    useEffect(() => {
        getMenuList();
        getTitleCardContactList();
        getTitleCardLinkList();
    }, []);

    return (
        <div className='main'>
            <div className='content-card'>

                {
                    (props.state.isTitleOpen || !screenType.isMobile) ?
                        <div className='title-card card'>
                            <div className='section'>
                                <img className='profile-pic' src='pic.jpg' alt='profile-pic'></img>
                                <span><strong>Soumyabrata Sarkar</strong></span>
                                <span>sarkar.soumyabrata2@gmail.com</span>
                            </div>
                            <ul>
                                {
                                    titleCardContactList !== undefined ?

                                        titleCardContactList.phoneNos.map((menu) => {
                                            return (
                                                <li>
                                                    <div className='list-item'>
                                                        <div className='icon'>
                                                        </div>
                                                        <div className='content'>
                                                            <span>{menu.phoneNo}</span>
                                                        </div>
                                                    </div>
                                                </li>
                                            )
                                        }) : <></>
                                }
                            </ul>
                        </div>
                        : <></>
                }

                {
                    (props.state.isMenuOpen || !screenType.isMobile) ?
                        <div className='sub-content-card card'>
                            <ul>
                                {menuList !== undefined ?
                                    menuList.map((menu, index) => {
                                        return (
                                            menu.title !== 'Phone' && menu.title !== 'Address' && menu.title !== 'Link' ?
                                                <li key={index}>
                                                    <div className='list-item'>
                                                        <div className='icon'>
                                                            {htmlPerser(iconMap[menu.title])}
                                                        </div>
                                                        <div className='content'>
                                                            <span>{menu.title}</span>
                                                        </div>
                                                        <div className='select'>
                                                            <svg xmlns='http://www.w3.org/2000/svg' fill='currentColor' className='icon' viewBox='0 0 16 16'>
                                                                <path fillRule='evenodd' d='M4.646 1.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1 0 .708l-6 6a.5.5 0 0 1-.708-.708L10.293 8 4.646 2.354a.5.5 0 0 1 0-.708z' />
                                                            </svg>
                                                        </div>
                                                    </div>
                                                </li>
                                                : <></>)
                                    })
                                    : <></>
                                }

                            </ul>
                        </div>
                        : <></>
                }

                {
                    (!props.state.isTitleOpen && !props.state.isMenuOpen) || !screenType.isMobile ?
                        <div className='sub-content-info-card card'>
                            sub content info
                        </div>
                        : <></>
                }
            </div>

        </div>
    )
}

export default Main
