import React, { useState, useEffect } from 'react'
import '../styles/Stepper.css'

function Stepper(props) {

    const [modSource, setModSource] = useState();

    useEffect(() => {
        if (props.source !== undefined) {

            let arr = [];
            let index = 0;

            props.source.forEach(element => {
                if (index == 0) {
                    arr.push({
                        'leftInfo': element.leftInfo,
                        'rightPrimaryInfo': element.rightPrimaryInfo,
                        'rightSecondaryInfo': element.rightSecondaryInfo,
                        'nodeClass': 'first-node'
                    })
                } else if (index == props.source.length - 1) {
                    arr.push({
                        'leftInfo': element.leftInfo,
                        'rightPrimaryInfo': element.rightPrimaryInfo,
                        'rightSecondaryInfo': element.rightSecondaryInfo,
                        'nodeClass': 'last-node'
                    })
                } else {
                    arr.push({
                        'leftInfo': element.leftInfo,
                        'rightPrimaryInfo': element.rightPrimaryInfo,
                        'rightSecondaryInfo': element.rightSecondaryInfo,
                        'nodeClass': 'node'
                    })
                }

                index++;
            })

            console.log(arr);
        }
    }, [])


    return (
        <div>
            {props.source !== undefined ?

                props.source.map((info, index) => {
                    return (
                        <div className="item" key={index}>
                            <div className="left-info">{info.leftInfo}</div>
                            <div className="node">
                                <div className="line"></div>
                                <div className="mark">
                                    <div className="dot"></div>
                                </div>
                            </div>
                            <div className="right-info">{info.rightPrimaryInfo}</div>
                        </div>
                    )
                }) : <></>}

        </div>
    )
}

export default Stepper
