import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState } from 'react';

function App() {

  const [isTitleCardOpen, setIsTitleCardOpen] = useState(true)
  const [isMenuCardOpen, setIsMenuCardOpen] = useState(false)
  const [isInfoCardOpen, setIsInfoCardOpen] = useState(false)

  useEffect(() => {
    setIsTitleCardOpen(false);
    setIsMenuCardOpen(false);
    setIsInfoCardOpen(false);
  }, [])


  const toggleCard = (cardName) => {
    setIsTitleCardOpen(false);
    setIsMenuCardOpen(false);
    setIsInfoCardOpen(false);

    switch (cardName) {
      case 'title':
        setIsTitleCardOpen(true);
        break;
      case 'menu':
        setIsMenuCardOpen(true);
        break;
      case 'info':
        setIsInfoCardOpen(true);
        break;
      default:
        console.log('Option not identified');
        break;
    }
  }

  return (
    <div>
      <Header toggleCard={toggleCard} />
      <Main state={{
        isTitleCardOpen: isTitleCardOpen,
        isMenuCardOpen: isMenuCardOpen,
        isInfoCardOpen: isInfoCardOpen
      }} />
    </div>
  );
}

export default App;
